import { useEffect, useState } from "react";
import "./transfer.css";

export default function TransferForm({
  fromAccount,
  accounts = [],
  onTransfer,
}) {
  const [account_From, setAccount_From] = useState(fromAccount || "");
  const [account_To, setAccount_To] = useState("");
  const [amount, setAmount] = useState("");
  const [currency, setCurrency] = useState("");
  const [date, setDate] = useState("");
  const [comment, setComment] = useState("");
  const [validation, setValidation] = useState(false);
  useEffect(() => {
    setAccount_From(fromAccount); // Update when prop changes
  }, [fromAccount]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const AccountData = {
      account_From,
      account_To,
      amount,
      currency,
      date,
      comment,
    };

    fetch("http://localhost:5000/accounts", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(AccountData),
    })
      .then(() => {
        alert("Transfer successful");
        onTransfer?.(AccountData); // notify parent
      })
      .catch((err) => console.error(err));
  };
  return (
    <div className="Container width-60">
      <h2>Transfer Form</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="account_from">Account From:</label>
        <input type="text" id="account_from" value={account_From} readOnly />

        <label htmlFor="account_To">Account To:</label>
        <select
          id="account_To"
          name="account_To"
          required
          value={account_To}
          onChange={(e) => setAccount_To(e.target.value)}
        >
          <option value="">-- Select Account --</option>
          {accounts &&
            accounts
              .filter((item) => item.name !== fromAccount) // exclude the "From" account
              .map((item) => (
                <option key={item.id} value={item.name}>
                  {item.name}
                </option>
              ))}
        </select>
        {account_To.length === 0 && validation && (
          <span className="errorMsg">Please select the destinated account</span>
        )}

        <label htmlFor="amount">Amount:</label>
        <input
          type="text"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          onMouseDown={() => setValidation(true)}
        />
        {amount.length === 0 && validation && (
          <span className="errorMsg">Enter the amount</span>
        )}

        <label htmlFor="currency">Currency:</label>
        <input
          type="text"
          id="currency"
          value={currency}
          onChange={(e) => setCurrency(e.target.value)}
        />

        <label htmlFor="date">Date:</label>
        <input
          type="date"
          id="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />

        <label htmlFor="comment">Comment:</label>
        <input
          type="text"
          id="comment"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />

        <button className="btn btn-transfer" type="submit">
          Transfer
        </button>
      </form>
    </div>
  );
}
