import React, { useState } from "react";
import TransferForm from "../Form/TransferForm";

const AccountList = ({ accounts }) => {
  const [selectedAccount, setSelectedAccount] = useState(null);

  const toggleForm = (account) => {
    setSelectedAccount((prev) =>
      prev && prev.id === account.id ? null : account
    );
  };

  return (
    <div className="relative">
      <div className="overflow-x-auto">
        <table className="table w-full">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Currency</th>
              <th>Balance</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {accounts &&
              accounts.map((item) => (
                <React.Fragment key={item.id}>
                  <tr>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.currency}</td>
                    <td>{item.balance}</td>
                    <td>
                      <button
                        onClick={() => toggleForm(item)}
                        className="btn-primary"
                      >
                        Transfer
                      </button>
                    </td>
                  </tr>
                </React.Fragment>
              ))}
          </tbody>
        </table>
      </div>

      {/* Floating Form */}
      {selectedAccount && (
        <div className="absolute top-0 left-0 w-full h-full z-50 bg-white bg-opacity-95 p-6 shadow-xl">
          <TransferForm fromAccount={selectedAccount.name} />
          <button
            onClick={() => setSelectedAccount(null)}
            className="mt-4 btn btn-secondary"
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

export default AccountList;
