import { NavLink } from "react-router-dom";
import "./sidebar.css";

export default function Sidebar() {
  return (
    <div className="sidebar">
      <h2>Menu</h2>
      <NavLink to="/" className={({ isActive }) => (isActive ? "active" : "")}>
        Account View
      </NavLink>
      <NavLink
        to="/transactions"
        className={({ isActive }) => (isActive ? "active" : "")}
      >
        Transaction Log
      </NavLink>
    </div>
  );
}
