import { useEffect, useState } from "react";
import "./account.css";

export default function AccountList() {
  const [accounts, setAccount] = useState("");
  useEffect(() => {
    // to get data from the API
    fetch("http://localhost:5000/accounts")
      // promise
      .then((res) => res.json())
      // to update data
      .then((data) => setAccount(data))
      .catch((err) => console.log(err.message));
  }, []);
  const toggleForm = (account) => {
    setSelectedAccount((prev) =>
      prev && prev.id === account.id ? null : account
    );
  };
  return (
    <div className="container width-60">
      <div className="table-container">
        <h2>Account List </h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Account_name</th>
              <th>Currency</th>
              <th>Amount</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {accounts &&
              accounts.map((item) => (
                <tr key={item.id}>
                  {" "}
                  {/* âœ… Add key here */}
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.currency}</td>
                  <td>{item.balance}</td>
                  <button onClick={() => item.id} className="btn btn-info ">
                    Transact
                  </button>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
